/*
 * Nap_NewPNode --
 *
 *	Create new Nap_PNode.
 */



static Nap_PNode *
Nap_NewPNode(
    NapClientData       *nap_cd,
    Nap_PNclass         class)          /* node type: NAP_VALUE, NAP_LIST, etc. */
{
    int			level;			/* parse level */
    Nap_PNode		*node;

    CHECK2NULL(Nap_ValidPNclass(class), "Nap_NewPNode: bad class");
    node = (Nap_PNode *) MALLOC(sizeof(Nap_PNode));
    CHECK2NULL(node, "Nap_NewPNode: Error calling MALLOC");
    node->class = class;
    level = nap_cd->parseLevel;
    assert(level >= 0);
    assert(level < nap_cd->recursionLimit);
    node->scanPtr = nap_cd->token[level];
    node->str = NULL;
    node->op = NULL_OP;
    node->left = NULL;
    node->right = NULL;
    node->repCount = 0;
    node->list = NULL;
    node->next = NULL;
    node->tail = NULL;
    ++ nap_cd->numberPNodes;
    return node;
}

/*
 * FreePNode --
 *
 *	Free Nap_PNode.
 */



void
FreePNode(
    NapClientData       *nap_cd,
    Nap_PNode		*node)
{
    assert(Nap_ValidPNclass(node->class));
    FREE(node);
    -- nap_cd->numberPNodes;
}



Nap_PNode *
Nap_NewExprPNode(
    NapClientData	*nap_cd,
    Nap_PNode		*left,          /* left operand */
    int                 op,		/* operator */
    Nap_PNode		*right)		/* right operand */
{
    Nap_PNode           *node;

    node = Nap_NewPNode(nap_cd, NAP_EXPR);
    assert(node);
    node->op = op;
    node->left = left;
    node->right = right;
    return node;
}



