/*
 * Nap_NetcdfVarGetInfo --
 *
 *	Get rank, shape & netCDF (external) data-type
 *	Determine whether dimension 0 is unlimited. (If rank is 0 then false.)
 */



static int
Nap_NetcdfVarGetInfo(
    NapClientData       *nap_cd,
    int			ncid,			/* file handle */
    int			varid,			/* var handle */
    int			*rank,
    size_t		*shape,
    nc_type		*xtype,
    int			*isRecord)		/* Is dim 0 unlimited? (can be NULL) */
{
    int			dimids[NAP_MAX_RANK];
    int			dimid_unlim;		/* ID of unlimited dimension */
    int			i;
    size_t		n;
    int			status;

    assert(rank);
    assert(shape);
    assert(xtype);
    status = nc_inq_varndims(ncid, varid, rank);
    CHECK2NC(status, "Nap_NetcdfVarGetInfo: Error calling nc_inq_varndims");
    status = nc_inq_vardimid(ncid, varid, dimids);
    CHECK2NC(status, "Nap_NetcdfVarGetInfo: Error calling nc_inq_vardimid");
    for (i = 0; i < *rank; i++) {
	status = nc_inq_dimlen(ncid, dimids[i], &n);
	CHECK2NC(status, "Nap_NetcdfVarGetInfo: Error calling nc_inq_dimlen");
	shape[i] = n;
    }
    status = nc_inq_vartype(ncid, varid, xtype);
    CHECK2NC(status, "Nap_NetcdfVarGetInfo: Error calling nc_inq_vartype");
    if (isRecord) {
	if (*rank > 0) {
	    status = nc_inq_unlimdim(ncid, &dimid_unlim);
	    CHECK2NC(status, "Nap_NetcdfVarGetInfo: Error calling nc_inq_unlimdim");
	    *isRecord = dimids[0] == dimid_unlim;
	} else {
	    *isRecord = 0;
	}
    }
    return TCL_OK;
}


